import React from "react";
import classes from "./NoteList.module.css";
import NoteItem from "../NoteItem/NoteItem";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const NoteList = ({ notes, remove, changeNoteStatus, mode }) => {
    return (

            <Droppable droppableId={classes.noteList}>
                {(provided) => (
                    <div
                        className={classes.noteList}
                        {...provided.droppableProps}
                        ref={provided.innerRef}
                    >
                        <TransitionGroup>
                            {notes.map((note, index) => (
                                <CSSTransition
                                    key={note.id}
                                    timeout={400}
                                    classNames="note"
                                >
                                    <Draggable key={note.id} draggableId={(note.id.toString())} index={index}>
                                        {(provided) => (
                                            <NoteItem ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}
                                                note={note}
                                                remove={remove}
                                                changeNoteStatus={
                                                    changeNoteStatus
                                                }
                                                mode={mode}
                                            />
                                        )}
                                    </Draggable>
                                </CSSTransition>
                            ))}
                        </TransitionGroup>
                        {!notes.length && (
                            <h1 className={classes.noNotes__title}>No Notes</h1>
                        )}
                    </div>
                )}
            </Droppable>
    );
};

export default NoteList;
