import classes from "./Footer.module.css";
import { useState } from "react";

const Footer = ({ listLength, clear, mode, query, setQuery }) => {
    const footerClasses = [classes.footer];
    const [active, setActive] = useState(false);

    const onToggleClick = (newQuery) => {
        setActive(!active);
        setQuery(newQuery);
    };

    if (mode === "light") {
        footerClasses.push(classes.light);
    }

    return (
        <div className={footerClasses.join(" ")}>
            <div className={classes.footer__items__counter}>
                {listLength} items left
            </div>
            <div className={classes.footer__sort__queries}>
                <p className={`${active && query === 'all' ? `${classes.query_active}` : ''}`} onClick={onToggleClick('all')}>All</p>
                <p className={`${active && query === 'active' ? `${classes.query_active}` : ''}`} onClick={onToggleClick('active')}>Active</p>
                <p className={`${active && query === 'completed' ? `${classes.query_active}` : ''}`} onClick={onToggleClick('completed')}>Completed</p>
            </div>
            <div className={classes.footer__clear} onClick={() => clear()}>
                <p className={classes.footer__clear__text}>Clear Completed</p>
            </div>
        </div>
    );
};

export default Footer;
